struct Main {
  @State list: number[] = [3, 1, 4, 1, 5, 9, 2, 6, 5, 3];
  @State max: number = 0;
  @State min: number = 0;
  @State sum: number = 0;

  build() {
    Column() {
      Text(`List: ${this.list.toString()}`)
        .fontSize(18)
        .padding(10);

      Button('Find Max and Min')
        .onClick(() => {
          this.findMaxAndMin();
        })
        .margin(10);

      Text(`Max: ${this.max}`)
        .fontSize(18)
        .padding(10);

      Text(`Min: ${this.min}`)
        .fontSize(18)
        .padding(10);

      Text(`Sum of Max and Min: ${this.sum}`)
        .fontSize(18)
        .padding(10);
    }
    .width('100%')
    .height('100%')
    .padding(20)
    .backgroundColor('#ffffff');
  }

  findMaxAndMin() {
    if (this.list.length === 0) {
      this.max = 0;
      this.min = 0;
      this.sum = 0;
      return;
    }

    this.max = this.list[0];
    this.min = this.list[0];

    for (let i = 1; i < this.list.length; i++) {
      if (this.list[i] > this.max) {
        this.max = this.list[i];
      }
      if (this.list[i] < this.min) {
        this.min = this.list[i];
      }
    }

    this.sum = this.max + this.min;
  }
}

