  // Function to calculate the nth Catalan number
  calculateCatalanNumber(n: number): number {
    if (n <= 1) return 1
    let result = 0
    for (let i = 0; i < n; i++) {
      result += this.calculateCatalanNumber(i) * this.calculateCatalanNumber(n - i - 1)
    }
    return result
  }

  build() {
