  @State fibonacciResult: number = 0;

  // Function to calculate the nth Fibonacci number
  private calculateFibonacci(n: number): number {
    if (n <= 1) return n;
    let a = 0, b = 1;
    for (let i = 2; i <= n; i++) {
      let temp = a + b;
      a = b;
      b = temp;
    }
    return b;
  }

