    const charMap: Map<string, number> = new Map();
    let maxChar = '';
    let maxCount = 0;

    for (const char of str) {
      if (charMap.has(char)) {
        charMap.set(char, charMap.get(char)! + 1);
      } else {
        charMap.set(char, 1);
      }

      if (charMap.get(char)! > maxCount) {
        maxChar = char;
        maxCount = charMap.get(char)!;
