@Entry
@Component
struct SumOfTwoLargestNumbers {
  @State numbers: Array<number> = []
  @State sum: number = 0

  build() {
    Column() {
      Text('Input List of Numbers')
        .fontSize(24)
        .margin({ bottom: 20 })

      List() {
        ForEach(this.numbers, (number: number, index: number) => {
          ListItem() {
            Text(`Number ${index + 1}: ${number}`)
              .fontSize(18)
              .margin({ bottom: 10 })
          }
        })
      }
      .width('100%')
      .height(200)

      TextInput({ placeholder: 'Enter a number' })
        .type(InputType.Number)
        .onChange((value: string) => {
          const num = parseFloat(value)
          if (!isNaN(num)) {
            this.numbers.push(num)
            this.calculateSum()
          }
        })
        .width('80%')
        .margin({ bottom: 20 })

      Text(`Sum of Two Largest Numbers: ${this.sum}`)
        .fontSize(24)
        .margin({ top: 20 })

      Button('Clear List')
        .onClick(() => {
          this.numbers = []
          this.sum = 0
        })
        .width('80%')
        .margin({ top: 20 })
    }
    .width('100%')
    .height('100%')
    .padding(20)
  }

  calculateSum() {
    if (this.numbers.length < 2) {
      this.sum = 0
      return
    }

    const sortedNumbers = [...this.numbers].sort((a, b) => b - a)
    this.sum = sortedNumbers[0] + sortedNumbers[1]
