        Text('Smallest Substring:')
          .fontSize(18)
          .margin({ bottom: 10 });

        Text(this.result)
          .fontSize(20)
          .fontColor('#0000FF')
          .fontWeight(FontWeight.Bold);
      }
    }
    .width('100%')
    .height('100%')
    .padding(20)
    .backgroundColor('#FFFFFF');
  }

  private findSmallestSubstring() {
    const s = this.inputS;
    const t = this.inputT;

    if (!s || !t) {
      this.errorMessage = 'Both fields must be filled!';
      this.result = '';
      return;
    }

    const tMap = new Map<string, number>();
    for (const char of t) {
      tMap.set(char, (tMap.get(char) || 0) + 1);
    }

    let left = 0;
    let minLen = Infinity;
    let count = t.length;
    let minStart = 0;

    for (let right = 0; right < s.length; right++) {
      const char = s[right];
      if (tMap.has(char)) {
        tMap.set(char, tMap.get(char)! - 1);
        if (tMap.get(char)! >= 0) {
          count--;
        }
      }

      while (count === 0) {
        if (right - left + 1 < minLen) {
          minLen = right - left + 1;
          minStart = left;
        }

        const leftChar = s[left];
        if (tMap.has(leftChar)) {
          tMap.set(leftChar, tMap.get(leftChar)! + 1);
          if (tMap.get(leftChar)! > 0) {
            count++;
          }
        }
        left++;
      }
    }

    if (minLen !== Infinity) {
      this.result = s.substring(minStart, minStart + minLen);
      this.errorMessage = '';
    } else {
      this.errorMessage = 'No valid substring found!';
      this.result = '';
    }
  }
}

