        .margin({ top: 20 })
    }
    .width('100%')
    .height('100%')
    .padding(20)
    .backgroundColor('#ffffff')
  }

  // Function to find the smallest repeating cycle in a string
  private findSmallestRepeatingCycle(str: string): number {
    const n = str.length;
    for (let i = 1; i <= n / 2; i++) {
      if (n % i === 0) {
        const pattern = str.substring(0, i);
        let isPattern = true;
        for (let j = i; j < n; j += i) {
          if (str.substring(j, j + i) !== pattern) {
            isPattern = false;
            break;
          }
        }
        if (isPattern) {
          return i;
        }
      }
    }
    return n;
  }
}

