    calculateSwaps(): void {  
        let arr = this.numbers.slice();  
        let swapCount = 0;  
        for (let i = 0; i < arr.length; i++) {  
            while (arr[i] !== i + 1) {  
                let temp = arr[i];  
                arr[i] = arr[temp - 1];  
                arr[temp - 1] = temp;  
                swapCount++;  
            }  
        }  
        this.swaps = swapCount;  
        this.sortedList = arr;  
    }  

    build() {  
        Column({ space: 10 }) {  
            TextInput({ placeholder: 'Enter numbers separated by commas' })  
