    }
    .padding(20)
  }

  private calculateMaxVowels(str: string): number {
    const vowels = ['a', 'e', 'i', 'o', 'u'];
    let maxCount = 0;
    let currentCount = 0;

    for (let i = 0; i < str.length; i++) {
      const char = str[i].toLowerCase();
      if (vowels.includes(char)) {
        currentCount++;
        if (currentCount > maxCount) {
          maxCount = currentCount;
        }
      } else {
        currentCount = 0;
      }
    }

    return maxCount;
  }
}

