        .fontSize(16)
        .margin({ bottom: 20 });
    }
    .width('100%')
    .height('100%')
    .padding(20)
  }

  private maxSumIndex: number = 0;

  private findMaxSumSubarray(arr: number[], k: number): number {
    let maxSum = 0;
    let currentSum = 0;

    for (let i = 0; i < k; i++) {
      currentSum += arr[i];
    }

    maxSum = currentSum;
    this.maxSumIndex = 0;

    for (let i = k; i < arr.length; i++) {
      currentSum += arr[i] - arr[i - k];
      if (currentSum > maxSum) {
        maxSum = currentSum;
        this.maxSumIndex = i - k + 1;
      }
    }

    return maxSum;
  }
}

