    .justifyContent(FlexAlign.Center)
    .padding(20)
  }

  countCoprimes(n: number): number {
    let count = 0
    for (let i = 1; i < n; i++) {
      if (this.gcd(i, n) === 1) {
        count++
      }
    }
    return count
  }

  gcd(a: number, b: number): number {
    while (b !== 0) {
      let temp = b
      b = a % b
      a = temp
    }
    return a
  }
}

