          this.calculateArea();
        })
        .width('80%')
        .margin({ bottom: 10 });

      TextInput({ placeholder: 'Enter side B' })
        .type(InputType.Number)
        .onChange((value: string) => {
          this.sideB = value;
          this.calculateArea();
        })
        .width('80%')
        .margin({ bottom: 10 });

      TextInput({ placeholder: 'Enter side C' })
        .type(InputType.Number)
        .onChange((value: string) => {
          this.sideC = value;
          this.calculateArea();
        })
        .width('80%')
        .margin({ bottom: 20 });

      Text('Area: ' + this.area)
        .fontSize(24)
