  @State private steps: number = 0;
  @State private result: number = 0;

  private calculateWaysToClimb(steps: number): number {
    if (steps <= 1) return 1;
    let dp: number[] = new Array(steps + 1).fill(0);
    dp[0] = 1;
    dp[1] = 1;
