struct ArithmeticSliceCounter {
  @State numbers: number[] = [1, 2, 3, 4, 5, 6];
  @State sliceCount: number = 0;

  calculateArithmeticSlices(nums: number[]): number {
    let total = 0;
    let count = 0;
    for (let i = 2; i < nums.length; i++) {
      if (nums[i] - nums[i - 1] === nums[i - 1] - nums[i - 2]) {
        count++;
        total += count;
      } else {
        count = 0;
      }
    }
    return total;
  }

  build() {
