        });

      Button('Search')
        .width('90%')
        .height(40)
        .margin({ bottom: 10 })
        .onClick(() => {
          this.updateResult();
        });

      Text('Sorted List: ' + this.sortedList.join(', '))
        .fontSize(16)
        .margin({ bottom: 10 });

      Text(this.result)
        .fontSize(18)
        .fontWeight(FontWeight.Medium)
        .margin({ bottom: 20 });
    }
    .width('100%')
    .height('100%')
    .padding(20)
    .backgroundColor('#F5F5F5');
  }
}

