                nextIndex = i
              }
            }
            this.currentIndex = nextIndex
            this.jumps++
            this.steps.push(this.currentIndex)
          }
        })
        .margin({ top: 20, bottom: 20 })

      Text("Steps Taken: " + this.steps.join(" -> "))
        .fontSize(18)
        .margin({ top: 20, bottom: 20 })
    }
