  private calculateFibonacciSum(n: number): number {
    if (n <= 0) return 0;
    let a = 0, b = 1, sum = 0;
    for (let i = 1; i <= n; i++) {
      sum += a;
      const next = a + b;
      a = b;
      b = next;
    }
    return sum;
  }

  build() {
