struct SearchArrayApp {
  @State targetValue: number = 0
  @State array: number[] = [1, 2, 3, 4, 4, 4, 5, 6, 7, 8]
  @State result: number[] = [-1, -1]

  findFirstAndLastPosition(target: number, nums: number[]): number[] {
    let left = 0
    let right = nums.length - 1
    let first = -1
    let last = -1

    // Find first position
    while (left <= right) {
      let mid = Math.floor((left + right) / 2)
      if (nums[mid] === target) {
        first = mid
        right = mid - 1
      } else if (nums[mid] < target) {
        left = mid + 1
      } else {
        right = mid - 1
      }
    }

    // Find last position
    left = 0
    right = nums.length - 1
    while (left <= right) {
      let mid = Math.floor((left + right) / 2)
      if (nums[mid] === target) {
        last = mid
        left = mid + 1
      } else if (nums[mid] < target) {
        left = mid + 1
      } else {
        right = mid - 1
      }
    }

    return [first, last]
  }

  search() {
    this.result = this.findFirstAndLastPosition(this.targetValue, this.array)
  }

  build() {
