  @State inputValue: string = ''
  @State result: string = ''
  @State errorMessage: string = ''

  build() {
    Column() {
      Text('Find Minimum Perfect Squares Sum')
        .fontSize(24)
        .fontWeight(FontWeight.Bold)
        .margin({ bottom: 20 })

      TextInput({ placeholder: 'Enter a number' })
        .type(InputType.Number)
        .width('80%')
        .onChange((value: string) => {
          this.inputValue = value
          this.errorMessage = ''
        })
        .margin({ bottom: 20 })

      if (this.errorMessage) {
        Text(this.errorMessage)
          .fontColor('#FF0000')
          .margin({ bottom: 20 })
      }

      Button('Calculate')
        .onClick(() => {
          const n = parseInt(this.inputValue)
          if (isNaN(n) || n < 1) {
            this.errorMessage = 'Please enter a valid positive number.'
            this.result = ''
          } else {
            this.errorMessage = ''
            this.result = `Minimum perfect squares sum: ${this.numSquares(n)}`
          }
        })
        .width('80%')
        .margin({ bottom: 20 })

      if (this.result) {
        Text(this.result)
          .fontSize(18)
          .fontColor('#4CAF50')
      }
    }
    .width('100%')
    .height('100%')
    .padding(20)
    .justifyContent(FlexAlign.Center)
  }

  numSquares(n: number): number {
    const dp: number[] = new Array(n + 1).fill(Infinity)
    dp[0] = 0

    for (let i = 1; i <= n; i++) {
      for (let j = 1; j * j <= i; j++) {
        dp[i] = Math.min(dp[i], dp[i - j * j] + 1)
      }
    }

    return dp[n]
  }
}

