        .onChange((value: string) => {
          this.num2 = parseInt(value);
        });

      Button('Calculate SCM')
        .width('80%')
        .height(40)
        .margin(10)
        .onClick(() => {
          this.result = this.findSmallestCommonMultiple(this.num1, this.num2);
        });

