  findIntersection() {
    let i = 0, j = 0;
    this.intersection = [];
    while (i < this.arr1.length && j < this.arr2.length) {
      if (this.arr1[i] < this.arr2[j]) {
        i++;
      } else if (this.arr1[i] > this.arr2[j]) {
        j++;
      } else {
        this.intersection.push(this.arr1[i]);
        i++;
        j++;
      }
