          this.errorMessage = ''
        })
        .margin({ bottom: 20 })

      Button('Find Quadruplets')
        .onClick(() => this.findQuadruplets())
        .width('80%')
        .margin({ bottom: 20 })

      if (this.errorMessage) {
        Text(this.errorMessage)
          .fontColor('#FF0000')
          .margin({ bottom: 20 })
      }

      if (this.results.length > 0) {
        Text('Quadruplets:')
          .fontSize(18)
          .fontWeight(FontWeight.Bold)
          .margin({ bottom: 10 })

        List() {
          ForEach(this.results, (quadruplet: Array<number>) => {
            ListItem() {
              Text(quadruplet.join(', '))
                .fontSize(16)
                .margin({ bottom: 5 })
            }
          })
        }
        .width('100%')
        .height(200)
      }
    }
    .width('100%')
    .height('100%')
    .padding(20)
  }

  findQuadruplets() {
    const nums = this.inputArray.split(',').map(Number)
    if (nums.some(isNaN)) {
      this.errorMessage = 'Invalid input. Please enter numbers only.'
      this.results = []
      return
    }

    const n = nums.length
    const result: Array<Array<number>> = []
    nums.sort((a, b) => a - b)

    for (let i = 0; i < n - 3; i++) {
      if (i > 0 && nums[i] === nums[i - 1]) continue
      for (let j = i + 1; j < n - 2; j++) {
        if (j > i + 1 && nums[j] === nums[j - 1]) continue
        let left = j + 1
        let right = n - 1
        while (left < right) {
          const sum = nums[i] + nums[j] + nums[left] + nums[right]
          if (sum === 0) {
            result.push([nums[i], nums[j], nums[left], nums[right]])
            while (left < right && nums[left] === nums[left + 1]) left++
            while (left < right && nums[right] === nums[right - 1]) right--
            left++
            right--
          } else if (sum < 0) {
            left++
          } else {
            right--
          }
        }
      }
    }

    this.results = result
  }
}

