      Button('Calculate')
        .onClick(() => {
          const num = parseInt(this.inputNumber)
          if (!isNaN(num) && num > 0) {
            this.result = this.findLargestPrimeFactor(num).toString()
          } else {
            this.result = 'Invalid input'
          }
        })
        .width('80%')
        .margin({ bottom: 20 })

      Text(`Result: ${this.result}`)
        .fontSize(20)
        .fontColor('#4CAF50')
    }
    .width('100%')
    .height('100%')
    .padding(20)
  }

  findLargestPrimeFactor(n: number): number {
    let largestFactor = -1

    while (n % 2 === 0) {
      largestFactor = 2
      n = n / 2
    }

    for (let i = 3; i <= Math.sqrt(n); i += 2) {
      while (n % i === 0) {
        largestFactor = i
        n = n / i
      }
    }

    if (n > 2) {
      largestFactor = n
    }

    return largestFactor
  }
}

