  private isSubsequence(list1: string[], list2: string[]): boolean {
    this.index1 = 0;
    this.index2 = 0;

    while (this.index1 < list1.length && this.index2 < list2.length) {
      if (list1[this.index1] === list2[this.index2]) {
        this.index1++;
      }
      this.index2++;
    }

    return this.index1 === list1.length;
  }
}

