  private timerId: number = -1;

  // Function to start the countdown timer
  private startTimer() {
    if (!this.isRunning) {
      this.isRunning = true;
      this.timerId = setInterval(() => {
        if (this.timeLeft > 0) {
          this.timeLeft -= 1;
        } else {
          this.stopTimer();
        }
      }, 1000); // Update every second
    }
  }

  // Function to stop the countdown timer
