  sortOddIndices(list: number[]): number[] {
    let oddIndices = list.filter((_, index) => index % 2 !== 0)
    oddIndices.sort((a, b) => a - b)
    let result = [...list]
    let oddIndex = 0
    for (let i = 1; i < result.length; i += 2) {
      result[i] = oddIndices[oddIndex++]
    }
    return result
  }
}

