    .padding(20)
    .backgroundColor('#ffffff')
  }

  calculateSingleDigitSum(num: string): string {
    let sum = 0;
    for (let char of num) {
      sum += parseInt(char);
    }
    while (sum >= 10) {
      let tempSum = 0;
      for (let char of sum.toString()) {
        tempSum += parseInt(char);
      }
      sum = tempSum;
    }
    return sum.toString();
  }
}

