  calculateFibonacci(position: number): Array<number> {
    let sequence = [0, 1]
    for (let i = 2; i <= position; i++) {
      sequence.push(sequence[i - 1] + sequence[i - 2])
    }
    return sequence.slice(0, position + 1)
  }

