        placeholder: 'Enter a string',
        text: this.inputString
      })
      .width('80%')
      .height(50)
      .backgroundColor('#f5f5f5')
      .onChange((value: string) => {
        this.inputString = value;
      })
      .margin({ bottom: 20 });

      Button('Find Subsequence')
        .width('60%')
        .height(50)
        .fontSize(20)
        .onClick(() => {
          this.result = this.findSmallestRepeatingSubsequence(this.inputString);
          if (this.result) {
            this.message = `Smallest Repeating Subsequence: ${this.result}`;
          } else {
            this.message = 'No repeating subsequence found.';
          }
        })
        .margin({ bottom: 20 });

      if (this.message) {
        Text(this.message)
          .fontSize(20)
          .fontColor('#4CAF50')
          .fontWeight(FontWeight.Bold)
          .margin({ top: 20 });
      }
    }
    .width('100%')
    .height('100%')
    .justifyContent(FlexAlign.Center)
    .padding(20);
  }

  private findSmallestRepeatingSubsequence(s: string): string {
    const n = s.length;
    for (let i = 1; i <= n / 2; i++) {
      if (n % i === 0) {
        const pattern = s.substring(0, i);
        let repeated = '';
        for (let j = 0; j < n / i; j++) {
          repeated += pattern;
        }
        if (repeated === s) {
          return pattern;
        }
      }
    }
    return '';
  }
}

