  reverseNumber(num: number): number {
    let reversed = 0
    let isNegative = num < 0
    num = Math.abs(num)

    while (num > 0) {
      reversed = reversed * 10 + (num % 10)
      num = Math.floor(num / 10)
    }

    return isNegative ? -reversed : reversed
  }
}

