        })
        .width('50%')
        .margin({ top: 20 })
    }
    .width('100%')
    .height('100%')
    .padding(20)
  }

  findLargestSubarray() {
    const arr = this.inputArray.split('').map(Number);
    const map = new Map<number, number>();
    map.set(0, -1);
    let maxLen = 0;
    let endIndex = -1;
    let sum = 0;

    for (let i = 0; i < arr.length; i++) {
      sum += (arr[i] === 0) ? -1 : 1;

      if (map.has(sum)) {
        if (maxLen < i - map.get(sum)!) {
          maxLen = i - map.get(sum)!;
          endIndex = i;
        }
      } else {
        map.set(sum, i);
