  findSmallestDivisibleNumber(n: number): number {
    let lcm = 1
    for (let i = 2; i <= n; i++) {
      lcm = this.lcm(lcm, i)
    }
    return lcm
  }

  lcm(a: number, b: number): number {
    return (a * b) / this.gcd(a, b)
  }

  gcd(a: number, b: number): number {
    while (b !== 0) {
      let temp = b
      b = a % b
      a = temp
    }
    return a
  }
}

