  @State waysToClimb: number = 0

  // Function to calculate the number of ways to climb the stairs
  calculateWays(n: number): number {
    if (n <= 1) return 1
    let dp: number[] = new Array(n + 1)
    dp[0] = 1
    dp[1] = 1
    for (let i = 2; i <= n; i++) {
      dp[i] = dp[i - 1] + dp[i - 2]
    }
    return dp[n]
  }

  build() {
    Column() {
      Text("Stair Climbing Problem")
        .fontSize(24)
        .fontWeight(FontWeight.Bold)
