          .width('100%')
          .height(40)
          .margin({ bottom: 20 })
          .backgroundColor('#f5f5f5')
          .padding({ left: 12 })
          .onChange((value: string) => {
            this.inputNumber = value;
          });
      }

      // Calculate Button
      Button('Calculate')
        .width('100%')
        .height(50)
        .fontSize(18)
        .backgroundColor('#007AFF')
        .onClick(() => {
          const num = parseInt(this.inputNumber);
          if (!isNaN(num)) {
            this.onesCount = this.countOnes(num);
            this.showResult = true;
          } else {
            promptAction.showToast({
